	private static void burn(String wallet) throws Exception {
		double amt = chain.rusd().getPosition(wallet);
		if (amt >= .005) {
			chain.rusd().burnRusd(wallet, amt, chain.getAnyStockToken() ).waitForReceipt();
		}
	}
